#pragma config(Motor,  port2,           FLmotor,       tmotorVex269, openLoop)
#pragma config(Motor,  port3,           FRmotor,       tmotorVex269, openLoop, reversed)
#pragma config(Motor,  port4,           BLmotor,       tmotorVex269, openLoop)
#pragma config(Motor,  port5,           BRmotor,       tmotorVex269, openLoop, reversed)
