#pragma platform(VEX)

//Competition Control and Duration Settings
#pragma competitionControl(Competition)
#pragma autonomousDuration(15)
#pragma userControlDuration(145)

#include "Vex_Competition_Includes.c"   //Main competition background code...do not modify!
/* ROBOTICS CONTROL MANUAL FOR STEVE'S TEAM (name to be determined)
autonomous will move robot forward, back, pause, repeat
left joystick = left wheels
right joystick = right wheels
planning to simplify this soon */

void Drive(int S)
{
	motor[BLmotor]=S; //drive according to S (speed level)
	motor[BRmotor]=S;
	motor[FLmotor]=S;
	motor[FRmotor]=S;
}

void NDrive(int S)
{
	motor[BLmotor]=-S; //drive according to S (speed level)
	motor[BRmotor]=-S;
	motor[FLmotor]=-S;
	motor[FRmotor]=-S;
}

