/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 Autonomous Task
//
// This task is used to control your robot during the autonomous phase of a VEX Competition.
// You must modify the code to add your own robot specific commands here.
//
/////////////////////////////////////////////////////////////////////////////////////////

task autonomous()
{
  Drive(127);
  wait1Msec(1500);
  NDrive(127);
  wait1Msec(1500);
  Drive(0);
  wait1Msec(6000);
  Drive(127);
  wait1Msec(1500);
  NDrive(127);
  wait1Msec(1500);
  Drive(0);
}

/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 User Control Task
//
// This task is used to control your robot during the user control phase of a VEX Competition.
// You must modify the code to add your own robot specific commands here.
//
/////////////////////////////////////////////////////////////////////////////////////////

task usercontrol()
{
	while (true) //might want to change to 1==1 but i think this runs the same so
	{
	  //Right side of the robot is controlled by the right joystick, Y-axis
		motor[FRmotor] = vexRT[Ch2];
		motor[BRmotor]  = vexRT[Ch2];
		//Left side of the robot is controlled by the left joystick, Y-axis
		motor[FLmotor] = vexRT[Ch3];
		motor[BLmotor]  = vexRT[Ch3];
    //NOTE: in future make controls simpler and port to only one joystick?
	}
}
