//Main competition background code...do not modify!
#include "Vex_Competition_Includes.c"

bool isRoboticsFun = true;
string name;
float a = 3.0;
int b = 3;

void basicFunction(int g) {
	g = ceil(sin(pow((fabs(g)),2)));
}

task basicTask() {
	for (int i = 1;i <= 10;i++) {
		a = sqrt(i);
	}
}

