/*                                                                           */
/*                              User Control Task                            */
/*                                                                           */
/*  This task is used to control your robot during the user control phase of */
/*  a VEX Competition.                                                       */
/*                                                                           */
/*  You must modify the code to add your own robot specific commands here.   */
/*---------------------------------------------------------------------------*/

task usercontrol()
{
  // User control code here, inside the loop
	basicFunction(2);
  while (true)
  {
    motor[BL] = vexRT[Ch3];
    motor[FL] = vexRT[Ch3];
    motor[BR] = vexRT[Ch2];
    motor[FR] = vexRT[Ch2];

    if (vexRT[Btn5U] == 1) {
    	motor[lift] = 127;
  	} else if (vexRT[Btn6U] == 1) {
  		motor[lift] = -127;
  	} else {
  		motor[lift] = 0;
  	}
  }
}
